<!doctype html><html lang="en"><head><meta charset="UTF-8"><meta content="width=device-width,initial-scale=1"name="viewport"><title>Editar Usuario</title><link href="./css/leer.css"rel="stylesheet"><style>body{font-family:Arial,sans-serif;margin:0;padding:0;background-color:#f2f2f2}#header{background-color:#333;padding:10px;text-align:center}.logo{color:#fff;text-decoration:none;font-size:20px}.espacio-formulario{max-width:500px;margin:0 auto;padding:20px;background-color:#fff;margin-top:50px;box-shadow:0 0 10px rgba(0,0,0,.1)}h1{text-align:center;margin-bottom:30px}label{display:block;margin-bottom:10px;font-weight:700}input[type=email],input[type=password],input[type=text],select{width:100%;padding:10px;margin-bottom:20px;border:1px solid #ccc;border-radius:4px;box-sizing:border-box}select{height:40px}input[type=submit]{background-color:#333;color:#fff;padding:10px 20px;border:none;border-radius:4px;cursor:pointer}input[type=submit]:hover{background-color:#555}.error-message{color:red;margin-bottom:10px}.success-message{color:green;margin-bottom:10px}</style></head><body><header id="header"><a class="logo"href="./leer.php">Regresar</a></header><div class="espacio-formulario"><h1>Editar Usuario</h1><?php include 'conexion.php';$idUsuario=$_GET['id'];$sql="SELECT * FROM usuarios WHERE id_usuario = '$idUsuario'";$result=mysqli_query($conexion,$sql);$usuario=mysqli_fetch_assoc($result);if(!$usuario){echo "Usuario no encontrado.";}else{if($_SERVER['REQUEST_METHOD']==='POST'){$nombreCompleto=$_POST['nombre_completo'];$correo=$_POST['correo'];$usuario=$_POST['usuario'];$contrasena=$_POST['contrasena'];$tipo=$_POST['tipo'];$sql="UPDATE usuarios SET nombre_completo = '$nombreCompleto', correo = '$correo', usuario = '$usuario', contrasena = '$contrasena', tipo = '$tipo' WHERE id_usuario = '$idUsuario'";if(mysqli_query($conexion,$sql)){echo "<script>alert('Registro editado.'); window.location = './leer.php';</script>";exit();}else{echo "Error al actualizar el usuario: ".mysqli_error($conexion);}} ?><form action=""method="POST"><label for="nombre_completo">Nombre completo:</label> <input value="<?php echo $usuario['nombre_completo']; ?>"name="nombre_completo"required> <label for="correo">Correo:</label> <input value="<?php echo $usuario['correo']; ?>"name="correo"required type="email"> <label for="usuario">Usuario:</label> <input value="<?php echo $usuario['usuario']; ?>"name="usuario"required> <label for="contrasena">Contrase√±a:</label> <input value="<?php echo $usuario['contrasena']; ?>"name="contrasena"required type="password"> <label for="tipo">Tipo:</label> <select name="tipo"required><option value="admin"<?php if($usuario['tipo']=='admin')echo 'selected'; ?>>Administrador</option><option value="usuario"<?php if($usuario['tipo']=='usuario')echo 'selected'; ?>>Usuario</option></select> <input value="Guardar cambios"type="submit"></form><?php } ?></div></body></html>