<?php session_start();include 'conexion.php';$usuario=$_POST['usuario'];$contrasena=$_POST['contrasena'];$validar_login=mysqli_query($conexion,"SELECT * FROM usuarios WHERE usuario = '$usuario' AND contrasena = '$contrasena' ");if(mysqli_num_rows($validar_login)==1){$row=mysqli_fetch_assoc($validar_login);$tipo_usuario=$row['tipo'];$nombre_completo=$row['nombre_completo'];$primer_nombre=substr($nombre_completo,0,strpos($nombre_completo," "));$_SESSION['usuario']=$usuario;$_SESSION['tipo']=$tipo_usuario;$_SESSION['nombre_completo']=$nombre_completo;$_SESSION['primer_nombre']=$primer_nombre;header("Location: ../index.php");exit;}else{echo '<script>alert("Usuario no existe, por favor verifique los datos introducidos");window.location = "../login.php";</script>';exit;}